package com.trungthuc.services;import com.trungthuc.domains.Exam;import com.trungthuc.domains.Question;import com.trungthuc.repositories.QuestionRepository;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.data.domain.Page;import org.springframework.data.domain.Pageable;import org.springframework.data.jpa.domain.Specification;import org.springframework.stereotype.Service;import java.util.Optional;@Servicepublic class QuestionServiceImpl implements QuestionService {    @Autowired    QuestionRepository questionRepository;    @Override    public Question save(Question question) {        return questionRepository.save(question);    }    @Override    public Page<Question> findAll(Specification<Question> specification, Pageable pageable) {        return questionRepository.findAll(specification,pageable);    }    @Override    public Optional<Question> findById(int id) {        Optional<Question> question =questionRepository.findById(id);        if (!question.isPresent()){            return   question.empty();        }        return question;    }    @Override    public Page<Question> findByExam(Exam exam, Pageable pageable) {        return questionRepository.findByExam(exam,pageable);    }    @Override    public void delete(int id) {        questionRepository.deleteById(id);    }}